import React, { useEffect, useState } from "react";
import PaginationButton from "./PaginationButton";
import { useNavigate } from "react-router-dom";
import "../styles/planets.css";

const Planets = () => {
  const [response, setResponse] = useState({});
  const [totalPages, setTotalPages] = useState(0);
  const navigate = useNavigate();

  const getPlanets = () => {
    try {
      fetch("https://swapi.dev/api/planets")
        .then((response) => response.json())
        .then((data) => {
          setResponse(data);
          setTotalPages(data.count / data.results.length);
          console.log(data);
        });
    } catch (error) {
      console.error(error.message);
    }
  };

  const getPlanetsByPage = (page) => {
    try {
      fetch(`https://swapi.dev/api/planets/?page=${page}`)
        .then((response) => response.json())
        .then((data) => {
          setResponse(data);
        });
    } catch (error) {
      console.error(error.message);
    }
  };

  const paginationButtons = () => {
    const buttons = [];
    for (let iterator = 1; iterator <= totalPages; iterator++) {
      buttons.push(
        <PaginationButton
          key={iterator}
          onClick={() => getPlanetsByPage(iterator)}
          pageNumber={iterator}
        />
      );
    }
    return buttons;
  };

  useEffect(() => {
    getPlanets();
  }, []);

  return (
    <div className="container-fluid planet-home">
      <div className="row">
        <div className="col-12">
          <div className="header">Planets</div>
          <div className="planets-container">
            {response.results &&
              response.results.map((planet) => (
                <div
                  key={planet.name}
                  className="planet-card"
                  onClick={() =>
                    navigate(`/residents`, {
                      state: { residents: planet.residents },
                    })
                  }
                >
                  <h3>{planet.name}</h3>
                  <p>Population: {planet.population}</p>
                  <p>Climate: {planet.climate}</p>
                  <p>Terrain: {planet.terrain}</p>
                </div>
              ))}
          </div>
          <div className="container pagination-area">
            <div className="row">
              <div className="col-12">{paginationButtons()}</div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Planets;
