import React, { useEffect, useState } from "react";
import "./App.css";
import ResidentCard from "./ResidentCard";

function App() {
  const [response, setResponse] = useState({});
  const [pagination, setPagination] = useState({});
  const [planets, setPlanets] = useState([]);

  useEffect(() => {
    fetch("https://swapi.dev/api/planets")
      .then((response) => response.json())
      .then((data) => {
        setResponse(data);
        setPagination(data.count / data.results.length);
        setPlanets(data.results);
      })
      .catch((error) => console.log(error));
  }, []);

  const callCurrentSelectedPage = (page) => {
    let url = `https://swapi.dev/api/planets/?page=${page}`;

    fetch(url)
      .then((response) => response.json())
      .then((data) => {
        setPlanets(data.results);
      })
      .catch((error) => console.log(error));
  };

  const createPagination = () => {
    let buttons = [];
    for (let i = 1; i <= pagination; i++) {
      buttons.push(
        <button onClick={() => callCurrentSelectedPage(i)} key={i}>
          {i}
        </button>
      );
    }
    return buttons;
  };

  return (
    <>
      <div className="card-container">
        {planets.map((planet) => (
          <div key={planet.name} className="card">
            <h2>{planet.name}</h2>
            <p>Climate: {planet.climate}</p>
            <p>Population: {planet.population}</p>
            <p>Terrain: {planet.terrain}</p>
            <div className="residents-container">
              {planet.residents.map((residentUrl) => (
                <ResidentCard key={residentUrl} residentUrl={residentUrl} />
              ))}
            </div>
          </div>
        ))}
      </div>
      <div>{createPagination()}</div>
    </>
  );
}

export default App;
